import React, { useEffect, useState } from "react";
import axios from "axios";
import BookForm from "./components/BookForm";
import BookList from "./components/BookList";

function App() {
    const [books, setBooks] = useState([]);

    useEffect(() => {
        axios.get("http://localhost:5000/books").then((res) => setBooks(res.data));
    }, []);

    const addBook = (book) => {
        axios.post("http://localhost:5000/books", book).then((res) => {
            setBooks([...books, res.data]);
        });
    };

    const deleteBook = (bookNumber) => {
        axios.delete(`http://localhost:5000/books/${bookNumber}`).then(() => {
            setBooks(books.filter((book) => book.bookNumber !== bookNumber));
        });
    };

    return (
        <div>
            <h1>Book Store</h1>
            <BookForm addBook={addBook} />
            <BookList books={books} deleteBook={deleteBook} />
        </div>
    );
}

export default App;
